        private void GenerateStructure()
        {
            int px = X - (Tiles.GetLength(1) / 2);
            int py = Y - (Tiles.GetLength(0) / 2);

            py = (int)(Y + Tiles.GetLength(0) * 1.3f);

            for (int x = 0; x < Tiles.GetLength(1); x++)
            {
                for (int y = 0; y < Tiles.GetLength(0); y++)
                {
                    byte tile = Tiles[Tiles.GetLength(0) - 1 - y, x];
                    short style = StyleSet[tile];
                    byte wall = Walls[Tiles.GetLength(0) - 1 - y, x];
                    byte slope = Slopes[Tiles.GetLength(0) - 1 - y, x];

                    if (tile != 0)
                        WorldGen.PlaceTile(px + x, py - 46 - y, TileSet[tile], false, true, -1, style);
                    WorldGen.PlaceWall(px + x, py - 46 - y, WallSet[wall]);

                    Framing.GetTileSafely(px + x, py - 46 - y).slope(slope);
                }
            }

            WorldGen.PlaceObject(px + 2, py - 46 - 5, Decimation.decimation.TileType("LockedShrineDoor"));
            WorldGen.PlaceObject(px + 67, py - 46 - 5, Decimation.decimation.TileType("LockedShrineDoor"));
            WorldGen.PlaceTile(px + 27, py - 46 - 44, TileID.SmallPiles, false, true, -1, 86);
            WorldGen.PlaceObject(px + 28, py - 46 - 44, TileID.Painting3X3, false, 14);
            WorldGen.PlaceObject(px + 41, py - 46 - 44, TileID.Painting3X3, false, 14);
            WorldGen.PlaceObject(px + 34, py - 46 - 44, TileID.Painting6X4, false, 14);
            WorldGen.PlaceObject(px + 10, py - 46 - 9, TileID.Painting3X3, false, 15);
            WorldGen.PlaceObject(px + 59, py - 46 - 9, TileID.Painting3X3, false, 15);
            WorldGen.PlaceBanner(px + 3, py - 46 - 15, TileID.Banners, 21);
            WorldGen.PlaceBanner(px + 66, py - 46 - 15, TileID.Banners, 21);
            WorldGen.PlaceTile(px + 33, py - 46 - 3, Decimation.decimation.TileType("ShrineAltar"));
        }

        public void Generate()
        {
            X = Main.maxTilesX / 2;

            do
            {
                Y = Main.maxTilesY - 110 + Main.rand.Next(20);
            } while (Y - 160 < 0);

            a = 75;
            b = 50;
            float c = (float)(Math.Sqrt(Math.Pow(a, 2) - Math.Pow(b, 2))); // Pythagore

            for (int x = -a - 10; x <= a + 10; x++)
            {
                for (int y = -b - 10; y <= b + 10; y++)
                {
                    double d1 = Vector2.Distance(new Vector2(-c, 0), new Vector2(x, y));
                    double d2 = Vector2.Distance(new Vector2(c, 0), new Vector2(x, y));

                    if (d1 + d2 >= 2 * a - 10 && d1 + d2 <= 2 * a + 10)
                    {
                        WorldGen.PlaceTile(X + x, Y + y, TileID.Ash, false, true);
                        if (Main.rand.Next(60) == 0)
                            WorldGen.TileRunner(X + x, Y + y, 5, 2, TileID.Obsidian, false);

                        Framing.GetTileSafely(X + x, Y + y).slope(0);
                    }
                    else if (d1 + d2 < 2 * a - 10)
                    {
                        Framing.GetTileSafely(X + x, Y + y).ClearEverything();
                    }
                }
            }

            a = 60;
            b = 12;
            c = (float)(Math.Sqrt(Math.Pow(a, 2) - Math.Pow(b, 2)));
            for (int x = -a; x <= a; x++)
                for (int y = -b + 3; y <= b; y++)
                    if (Vector2.Distance(new Vector2(-c, 0), new Vector2(x, y)) + Vector2.Distance(new Vector2(c, 0), new Vector2(x, y)) <= 2 * a)
                        WorldGen.PlaceTile(X + x, Y + y + Tiles.GetLength(1) / 2 - 3, Decimation.decimation.TileType("DeadEarth"), false, false);

            GenerateStructure();
        }
    }
